import React,{ createContext, useState } from "react"
import App from "./App"
const Firstname = createContext();
 const CompA = (props)=>{
    let [item, Set] = useState(0)
    function Inc(){
        item+=1
        Set(item)
    }
     return(
      <>
       <div style={{width:"400px",border:"2px solid black"}}>
           <h2> car {props.name}</h2>
           <h3>price  {props.price}</h3>
           <button onClick ={Inc}> add cart</button>
       </div>
       <Firstname.Provider value={item}>
            <App />
         </Firstname.Provider>
      </>
     )

 }

 export default CompA;
