import React, { useState } from "react"
import axios from "axios"

const Result = ()=>{
    const [movieList,Setlist] =useState([])
    const [input,Setinput] =useState("")
    async function Show(){

        let film ="heropanti";
        const data = await axios.get( `https://api.themoviedb.org/3/search/movie?api_key=15d2ea6d0dc1d476efbca3eba2b9bbfb&query=${film}`
        )

       Setlist(data.data.results)
       console.log(data.data)
     }
     Show()
return(

    <>
    <input type="text" placeholder="search movie" onChange={ (e)=>{Setinput(e.target.value)}}/>

         {
             movieList.map(  (item)=>{
                 return(
                     <>

                      <div className="container-fluid" style={{backgroundImage:"linear-gradient(to right,rgba(0,0,0,0.8) 100%, transparent 0%),url(img/bg.jpg)",height:"100vh",width:"100%"}}>



                       <div className="d-flex justify-content-around flex-wrap pb-0 mb-0">


                       <div className="pb-0 mb-0">
                           <h1> {item.title}</h1>
                     <img style={{width:"300px",height:"300px"}}
                     src={`http://image.tmdb.org/t/p/w500/${item.poster_path}`}
                      alt="poster"
                          className="img-fluid"
                      />

                           </div>

                       </div>
                      </div>




                     </>
                 )
             })

          }
    </>
)
}

export default Result
