import React, { useState } from "react"
import axios from "axios"

const Result = ()=>{
    const [movieList,Setlist] =useState([])
    const [input,Setinput] =useState("")
    async function Show(){

        const data = await axios.get( `https://api.themoviedb.org/3/search/movie?api_key=15d2ea6d0dc1d476efbca3eba2b9bbfb&query=${input}`
        )

       Setlist(data.data.results)
       console.log(data.data)
     }
     Show()

return(

    <>
    <div style={{background:"black"}} className="py-3">
    <input type="text"
     placeholder="search movie" 
     value={input}
     onChange={ (e)=>{Setinput(e.target.value)}}
        className="rounded-pill p-2"
     />
    </div>


         {
             movieList.map(  (item)=>{
                 return(
                     <>



                     <div className="container-fluid" style={{backgroundImage:"linear-gradient(to right,rgba(0,0,0,0.8) 100%, transparent 0%),url(img/bg.jpg)",height:"100vh",width:"100%"}}>
                      <div className="card" style={{width: "18rem"}}>

  <img src={`http://image.tmdb.org/t/p/w500/${item.poster_path}`} className="card-img-top" alt="poster" />
  <div className="card-body">
  <p className="card-text">Language -{item.overview}</p>

    <p className="card-text">Language -{item.original_language}</p>
    <p className="card-text">Release-{item.release_date}</p>
  </div>
</div>
</div>






                     </>
                 )
             })

          }
    </>
)
}

export default Result
